import React from 'react';
import css from './App.css';

class App extends React.Component {

  state = {
    tasks: [{
      id: 1,
      title: "Buy Milk",
      description: "order online on big basket",
      status: "todo"
    }, {
      id: 2,
      title: "book",
      description: "return library book",
      status: "todo"
    },
    {
      id: 3,
      title: "exercise",
      description: "do yoga and go on a walk",
      status: "todo"
    }, {
      id: 4,
      title: "water",
      description: "drink 4l water everyday",
      status: "todo"
    }, {
      id: 5,
      title: "cat",
      description: "feed the cat",
      status: "todo"
    }]
  };


  onDrag = (event, task) => {
   // alert(task.id);
  }

  onDrop = (event, task) => {
    alert(task.id);
  }

  render() {
    var tasks = {
      todo: [],
      inprocess: [],
      blocked: [],
      completed: []
    };

    this.state.tasks.forEach(task => {

      tasks[task.status].push(<div id={task.id} className="task" draggable="true" onDrag={(event) => this.onDrag(event, task)}>
          <div className="title">{task.title}</div>
          <div className="description">{task.description}</div>
      </div>)
    })

    return (
      <React.Fragment>
        <div class="kanban-board">
          <div class="kanban" onDrop={(event) => this.onDrop(event, "todo")}>
            <h1> To do</h1>
            <div> {tasks.todo}</div>
          </div>
          <div class="kanban" onDrop={(event) => this.onDrop(event, "inprocess")}>
            <h1> In process</h1>
          </div>
          <div class="kanban" onDrop={(event) => this.onDrop(event, "blocked")}>
            <h1> Blocked</h1>
          </div>
          <div class="kanban" onDrop={(event) => this.onDrop(event, "completed")}>
            <h1> Completed</h1>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
