import React from 'react';
//import TaskCard from './modules/taskcard';



class App extends React.Component {

  state = {

    tasks: [{
      title: "Buy Milk",
      description: "order online on big basket"
    }, {
      title: "book",
      description: "return library book"
    },
    {
      title: "exercise",
      description: "do yoga and go on a walk"
    }, {
      title: "water",
      description: "drink 4l water everyday"
    }, {
      title: "cat",
      description: "feed the cat"
    }]

  };

  render() {
		var tasks = {
	      inProgress: [],
	      Done: []
	    }

		this.state.tasks.forEach ((task) => {
		  tasks[task.type].push(
		    <div key={task.taskName} 
		      className="draggable"
		      style = {{backgroundColor: task.bgcolor}}>
		      {task.taskName}
		    </div>
		  );
		});

	    return (
	      <div className="drag-container">
	        <h2 className="head">To Do List Drag & Drop</h2>
		    <div className="inProgress">
	          <span className="group-header">In Progress</span>
	          {tasks.inProgress}
	        </div>
	        <div className="droppable">
	          <span className="group-header">Done</span>
	          {tasks.Done}
	        </div>	        
	      </div>
	    );
  	}
}

export default App;
