import React from 'react';
//import TaskCard from './modules/taskcard';



class App extends React.Component {

  state = {
    todo: [{
      title: "Buy Milk",
      description: "order online on big basket"
    }, {
      title: "book",
      description: "return library book"
    },
    {
      title: "exercise",
      description: "do yoga and go on a walk"
    }, {
      title: "water",
      description: "drink 4l water everyday"
    }, {
      title: "cat",
      description: "feed the cat"
    }],

    done: []
  };

  ondrag(){
    this.state.setSate("done", [])
  }

  render() {
    var tasks = [];
    this.state.todo.forEach((task) => {
      tasks.push(
        <div className="taskcard-content" draggable="true" onDrag={ondrag()}>
          <div className="title">
            {task.title}
          </div>
          <div className="description">
            {task.description}
          </div>
        </div>
      )
    });

    return (

      <React.Fragment>
        <div >
         {tasks}
        </div>

      </React.Fragment>

    );
  }
}

export default App;
