import React from 'react';
import css from './App.css';

//HTML Drag and Drop API

class App extends React.Component {
  state = {
    alltasks: {
      "todo": [{
        id: 1,
        title: "Buy Milk",
        description: "order online on big basket"
      },{
        id: 5,
        title: "cat",
        description: "feed the cat"
      }],
      "inprocess": [{
        id: 2,
        title: "book",
        description: "return library book"
      }],
      "blocked": [{
        id: 3,
        title: "exercise",
        description: "do yoga and go on a walk"
      }],
      "completed": [{
        id: 4,
        title: "water",
        description: "drink 4l water everyday"
      }]
    }
  };

  //1. first event on drag
  onDragStart = (event, taskid, taskstatus) => {
    event.dataTransfer.setData("taskid", taskid);
    event.dataTransfer.setData("taskstatus", taskstatus);
  }

  onDrop = (event) => {
    const taskid = event.dataTransfer.getData("taskid");
    const taskstatus = event.dataTransfer.getData("taskstatus");

    let draggedtask = this.state.tasks[taskstatus].filter((task) => {
      if (draggedtask.id == taskid) {
        return task;
      }
    });

    event.preventDefault()

  }

  onDragOver = (event) => {
    event.preventDefault();
  }

  render() {
    const alltasks = {
      todo: [],
      inprocess: [],
      blocked: [],
      completed: []
    };

    //set draggale to true
    //implement onDragStart

    let aKeys = Object.keys(this.state.alltasks);

    for (const key in aKeys) {
      const indtasks = this.state.alltasks[aKeys[key]];
      indtasks.forEach(task => {
        const taskstatus = aKeys[key];
        alltasks[taskstatus].push(<div id={task.id} className="task" draggable onDragStart={(event) => this.onDragStart(event, task.id, taskstatus)}>
          <div draggable="false" className="title">{task.title}</div>
          <div draggable="false" className="description">{task.description}</div>
        </div>)
      })
    }

    return (
      <React.Fragment>
        <h1 className="top-header">Kanban Board</h1>
        <div class="kanban-board">
          <div class="kanban">
            <h2> + To Do</h2>
            <div onDrop={(event) => this.onDrop(event, "todo")} onDragOver={(event) => this.onDragOver(event)}> {alltasks.todo}</div>
          </div>
          <div class="kanban">
            <h2> + In Process</h2>
            <div onDrop={(event) => this.onDrop(event, "inprocess")} onDragOver={(event) => this.onDragOver(event)}>{alltasks.inprocess}</div>
          </div>
          <div class="kanban" onDrop={(event) => this.onDrop(event, "blocked")} onDragOver={(event) => this.onDragOver(event)}>
            <h2> + Blocked</h2>
            <div onDrop={(event) => this.onDrop(event, "blocked")} onDragOver={(event) => this.onDragOver(event)}>{alltasks.blocked}</div>
          </div>
          <div class="kanban" onDrop={(event) => this.onDrop(event, "completed")} onDragOver={(event) => this.onDragOver(event)}>
            <h2> + Completed</h2>
            <div onDrop={(event) => this.onDrop(event, "completed")} onDragOver={(event) => this.onDragOver(event)}>{alltasks.completed}</div>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
