import React from 'react';
import css from './App.css';

//HTML Drag and Drop API

class App extends React.Component {
  state = {
    tasks: [{
      id: 1,
      title: "Buy Milk",
      description: "order online on big basket",
      status: "todo"
    }, {
      id: 2,
      title: "book",
      description: "return library book",
      status: "todo"
    },
    {
      id: 3,
      title: "exercise",
      description: "do yoga and go on a walk",
      status: "todo"
    }, {
      id: 4,
      title: "water",
      description: "drink 4l water everyday",
      status: "todo"
    }, {
      id: 5,
      title: "cat",
      description: "feed the cat",
      status: "todo"
    }]
  };

  onDragStart = (event, taskid) => {
    event.dataTransfer.setData("text/plain", taskid);
  }

  onDrop = (event) => {

    const taskid = event.dataTransfer.getData("text/plain");
    event.target.textContent = taskid;
    event.preventDefault()

  }

  onDragOver = (event) => {
    event.preventDefault();
  }

  render() {
    var tasks = {
      todo: [],
      inprocess: [],
      blocked: [],
      completed: []
    };

    //set draggale to true
    //implement onDragStart
    this.state.tasks.forEach(task => {
      tasks[task.status].push(<div id={task.id} className="task" draggable="true" onDragStart={(event) => this.onDragStart(event, task.id)}>
          <div className="title">{task.title}</div>
          <div className="description">{task.description}</div>
      </div>)
    })

    return (
      <React.Fragment>
        <h1 className="top-header">Kanban Board</h1>
        <div class="kanban-board">
          <div class="kanban" onDrop={(event) => this.onDrop(event, "todo")} onDragOver={(event) => this.onDragOver(event)}>
            <h2> 
              + To Do</h2>
            <div> {tasks.todo}</div>
          </div>
          <div class="kanban" onDrop={(event) => this.onDrop(event, "inprocess")} onDragOver={(event) => this.onDragOver(event)}>
            <h2> + In Process</h2>
          </div>
          <div class="kanban" onDrop={(event) => this.onDrop(event, "blocked")} onDragOver={(event) => this.onDragOver(event)}>
            <h2> + Blocked</h2>
          </div>
          <div class="kanban" onDrop={(event) => this.onDrop(event, "completed")} onDragOver={(event) => this.onDragOver(event)}>
            <h2> + Completed</h2>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
