import React from 'react';
import css from './App.css';

class App extends React.Component {

  state = {
    tasks: [{
      title: "Buy Milk",
      description: "order online on big basket",
      status: "todo"
    }, {
      title: "book",
      description: "return library book",
      status: "todo"
    },
    {
      title: "exercise",
      description: "do yoga and go on a walk",
      status: "todo"
    }, {
      title: "water",
      description: "drink 4l water everyday",
      status: "todo"
    }, {
      title: "cat",
      description: "feed the cat",
      status: "todo"
    }]
  };

  render() {

    var tasks = {
      todo: [],
      inprocess: [],
      blocked: [],
      completed: []
    };

    this.state.tasks.forEach(task => {
      <div>
        tasks[task.status].push(task.title);
      </div>

    })

    return (
      <React.Fragment>
        <div class="kanban-board">
          <div class="kanban">
            <h1> to do</h1>
            <div draggable="true"> {tasks.todo}</div>
          </div>
          <div class="kanban">
            <h1> in process</h1>
          </div>
          <div class="kanban">
            <h1> blocked</h1>
          </div>
          <div class="kanban">
            <h1> completed</h1>
          </div>
        </div>

      </React.Fragment>
    )

  }
}

export default App;
