import React from 'react';
import css from './App.css';

class App extends React.Component {

  state = {
    tasks: [{
      title: "Buy Milk",
      description: "order online on big basket",
      status: "todo"
    }, {
      title: "book",
      description: "return library book",
      status: "todo"
    },
    {
      title: "exercise",
      description: "do yoga and go on a walk",
      status: "todo"
    }, {
      title: "water",
      description: "drink 4l water everyday",
      status: "todo"
    }, {
      title: "cat",
      description: "feed the cat",
      status: "todo"
    }]
  };


  ondrag() {

  }

  render() {
    var tasks = {
      todo: [],
      inprocess: [],
      blocked: [],
      completed: []
    };

    this.state.tasks.forEach(task => {

      tasks[task.status].push(<div key={task.status} className="task" draggable="true">
          <div className="title">{task.title}</div>
          <div className="description">{task.description}</div>
      </div>)
    })

    return (
      <React.Fragment>
        <div class="kanban-board">
          <div class="kanban" onDrag={(event) => this.ondrag()}>
            <h1> To do</h1>
            <div> {tasks.todo}</div>
          </div>
          <div class="kanban">
            <h1> In process</h1>
          </div>
          <div class="kanban">
            <h1> Blocked</h1>
          </div>
          <div class="kanban">
            <h1> Completed</h1>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
